/*konversi milisekon menjadi format waktu normal*/
function convert(time=0) {                                  /*first elmt or default value*/
    let milisec = parseInt((time % 1000) / 100),            /*counting*/
    sec = Math.floor((time / 1000) & 60),
    min = Math.floor((time / (1000 * 60)) % 60),
    hr = Math.floor((time / (1000 * 60 * 60)) % 24);

    hr = hr < 10 ? "0" + hr : hr;                           /*formatting*/
    min = min < 10 ? "0" + min : min;
    sec = sec < 10 ? "0" + sec : sec;
    milisec = milisec < 10 ? "0" + milisec : milisec;

    return `${hr}:${min}:${sec}:${milisec}`;
}

console.log(convert(234792));                               /*testing*/